window.addEventListener("load",function(){function c(e){e.querySelectorAll(".remove").forEach(function(e){e.addEventListener("click",function(){this.parentNode.remove()})})}document.querySelectorAll(".dynamic-array-widget").forEach(function(i){c(i),i.querySelector(".add-array-item").addEventListener("click",function(){var e=i.querySelector(".array-item"),t=e.cloneNode(!0),r=t.querySelector("input").getAttribute("id").split("_"),n=r.slice(0,-1).join("_")+"_"+String(parseInt(r.slice(-1)[0])+1);t.querySelector("input").setAttribute("id",n),t.querySelector("input").value="",c(t),e.parentElement.appendChild(t)})})});
