$(document).ready(function(){$(".fancybox").fancybox({nextEffect:'none',prevEffect:'none',helpers:{title:{type:'inside'}}});});function showImage(channelName,tRanges,imageType,captions){for(var tIndex in tRanges){var idBase=channelName+"_"+tRanges[tIndex];var fileBase=channelName+"-"+imageType+"-"+tRanges[tIndex];document.getElementById("a_"+idBase).href="plots/"+fileBase+".png";document.getElementById("a_"+idBase).title=captions[tIndex];document.getElementById("img_"+idBase).src="plots/"+fileBase+".png";document.getElementById("img_"+idBase).alt=fileBase+".png";};};